(897, 195, '_form', '<div class=\"qs__form\">\n                        <div class=\"qs__row\">\n                            <div class=\"qs__col-6\">\n                                <div class=\"qs_form-group\">\n                                    <label class=\"qs__form-label\" for=\"name\">Họ và Tên <span>*</span></label>\n                                    [text* yourname class:qs__form-control autocomplete:name  placeholder \"John David\"]\n                                </div>\n                            </div>\n                            <div class=\"qs__col-6\">\n                                <div class=\"qs_form-group\">\n                                    <label class=\"qs__form-label\" for=\"name\">Email <span>*</span></label>\n                                    [email* email class:qs__form-control autocomplete:email  placeholder \"example@yourmail.com\"]\n                                </div>\n                            </div>\n                            <div class=\"qs__col-12\">\n                                <div class=\"qs_form-group2\">\n                                    <label class=\"qs__form-label\" for=\"name\">Thắc mắc của bạn <span>*</span></label>\n                                    [textarea* messages class:qs__form-control autocomplete:email 0x3  placeholder \"Mô tả vấn đề của bạn trong ít nhất 250 ký tự\"]\n                                </div>\n                            </div>\n                            <div class=\"qs__col-12\">\n                                <div class=\"qs_form-group3\">\n                                     <button class=\"qs__form-btn\" type=\"submit\">Gửi thông tin<img src=\"http://localhost/wordpress/wp-content/themes/my_network/assets/images/question/qs-btn.png\" alt=\"\"></button>\n                                </div>\n                            </div>\n                        </div>\n                    </div>'),
