(314, 1, '2023-02-08 20:41:31', '2023-02-08 13:41:31', 'a:8:{s:8:\"location\";a:1:{i:0;a:1:{i:0;a:3:{s:5:\"param\";s:9:\"post_type\";s:8:\"operator\";s:2:\"==\";s:5:\"value\";s:4:\"post\";}}}s:8:\"position\";s:6:\"normal\";s:5:\"style\";s:7:\"default\";s:15:\"label_placement\";s:3:\"top\";s:21:\"instruction_placement\";s:5:\"label\";s:14:\"hide_on_screen\";s:0:\"\";s:11:\"description\";s:0:\"\";s:12:\"show_in_rest\";i:0;}', 'Hỏi đáp', 'hoi-dap', 'trash', 'closed', 'closed', '', 'group_63e3a56fcb359__trashed', '', '', '2023-02-09 11:19:35', '2023-02-09 04:19:35', '', 0, 'http://localhost/wordpress/?post_type=acf-field-group&#038;p=314', 0, 'acf-field-group', '', 0),
